    public String geraNome() {
        return nomes[(new Random().nextInt(nomes.length))];
    }
    
    public String geraDominio() {
        return email[new Random().nextInt(email.length)];
    }
    
    public String geraEmail(String nome, String dominio) {
        return removerAcentos(nome).replaceAll(" ", "").concat(String.valueOf(new Random().nextInt(100)) + geraDominio()).toLowerCase();
    }
    
    public static String removerAcentos(String str) {
        return Normalizer.normalize(str, Normalizer.Form.NFD).replaceAll("[^\\p{ASCII}]", "");
    }
